mapper.select:
SELECT *
FROM
    t_ds_process_template
WHERE
    project_id = #{projectId}
    AND name = #{processTemplateName}

SELECT pt.id, pt.name, pt.biz_type_id, pt.project_id, pt.user_id, pt.version, pt.release_state, pt.flag, pt.create_time, pt.update_time, pt.modify_by, pt.description, pt.global_params, pt.tenant_id, u.user_name FROM t_ds_process_template pt LEFT JOIN t_ds_user u ON pt.user_id = u.id WHERE pt.project_id = #{projectId}
AND pt.name LIKE CONCAT('%', #{searchVal}, '%')
AND pt.user_id = #{userId}
ORDER BY pt.update_time desc
